import React from 'react';
import './router-prop.css';

class routerProp extends React.Component {
    constructor() {
        super();
    }

    render() {
        return <div className="App">
            <header className="App-header">
                <p>({this.props.location.state.name})</p>
                <p>每天学习一点，每天进步一点</p>
            </header>
        </div>;
    }
}

export default routerProp;
